CREATE TABLE food_delivery.product (
                                       id uuid NOT NULL DEFAULT food_delivery.uuid_generate_v4(),
                                       "name" varchar NULL,
                                       menu_id uuid NULL,
                                       description varchar NULL,
                                       unit varchar NULL,
                                       price numeric(12, 2) NULL,
                                       active bool NULL,
                                       created_at timestamp NULL DEFAULT CURRENT_TIMESTAMP,
                                       CONSTRAINT product_id_menu_id_key UNIQUE (id, menu_id),
                                       CONSTRAINT product_pkey PRIMARY KEY (id),
                                       CONSTRAINT fk_menu FOREIGN KEY (menu_id) REFERENCES food_delivery.menu(id)
);
